import math

def pooled_standard_dev(n, stdev1, stdev2):
    numer = (n-1)*(stdev1**2) + (n-1)*(stdev2**2)
    denom = n*2 - 2
    fract = numer / denom
    return math.sqrt(fract)

# n is the same for both because there are no refusals for me
def calculate_cohens_d(mean1, mean2, stdev1, stdev2):
    n = 2019
    numer = mean1 - mean2
    pooled_std_dev = pooled_standard_dev(n, stdev1, stdev2)
    cohens_d = numer / pooled_std_dev

    print(f"Cohen's D: {cohens_d}")


def t_stat(mean1, mean2, stdev1, stdev2):
    mean = mean1 - mean2

    denom = math.sqrt(((stdev1**2)/1265) + ((stdev2**2)/1303))
    ans = mean/denom
    print(f"Test Statistic: {ans}")


